import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Seat } from '../models/seat.model'; // Assuming you have created a model

@Component({
  selector: 'app-seat-selection',
  templateUrl: './seat-selection.component.html',
  styleUrls: ['./seat-selection.component.css']
})
export class SeatSelectionComponent implements OnInit {
  movie: any;
  cinema: any;
  date: any;
  showtime: any;
  seats: Seat[] = [];
  selectedSeats: Set<string> = new Set();

  constructor(private route: ActivatedRoute, private router: Router) {}

  ngOnInit(): void {
    this.initializeSeats();
  }

  initializeSeats(): void {
    const rows = ['A', 'B', 'C', 'D', 'E', 'F'];
    rows.forEach(row => {
      for (let i = 1; i <= 10; i++) {
        this.seats.push({ row: row, number: i, isTaken: Math.random() < 0.2 }); // 20% chance the seat is taken
      }
    });
  }

  toggleSeatSelection(seat: Seat): void {
    const seatId = `${seat.row}${seat.number}`;
    if (seat.isTaken) {
      console.warn('Seat is already taken.');
      return;
    }
    if (this.selectedSeats.has(seatId)) {
      this.selectedSeats.delete(seatId);
    } else {
      this.selectedSeats.add(seatId);
    }
  }

  isSeatSelected(seat: Seat): boolean {
    return this.selectedSeats.has(`${seat.row}${seat.number}`);
  }
}
