import { Component, OnInit } from '@angular/core';
// Assuming MovieService and other required imports

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any;
  dates: Date[] = []; // Populate accordingly
  cinemas: any[] = []; // Populate accordingly
  selectedDate: Date | null = null;
  selectedShowtime: string | null = null;
  numberOfTickets: number = 1;

  constructor(private router: Router) {}

  ngOnInit() {
    // Initialization code here
  }

  selectDate(date: Date) {
    this.selectedDate = date;
  }

  selectShowtime(showtime: string, cinema: any) {
    this.selectedShowtime = showtime;
    // Any other logic to handle after selecting a showtime
  }

  goToSeatSelection() {
    // Pass the required data to the seat selection component
    this.router.navigate(['/seat-selection'], { state: { date: this.selectedDate, showtime: this.selectedShowtime, tickets: this.numberOfTickets } });
  }
}
