import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { map } from 'rxjs/operators';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any = null; // Initialize to null to help identify if it’s not loaded
  dates: Date[] = [];
  cinemas = [
    { name: 'Cinema One', showtimes: ['9:00 AM', '12:00 PM', '3:00 PM', '6:00 PM'] },
    { name: 'Cinema Two', showtimes: ['10:00 AM', '1:00 PM', '4:00 PM', '7:00 PM'] }
  ];

  constructor(private route: ActivatedRoute, private router: Router, private http: HttpClient) {}

  ngOnInit(): void {
    this.initDates();
    this.fetchMovieDetails();
  }

  initDates() {
    for (let i = 0; i < 7; i++) {
      let newDate = new Date();
      newDate.setDate(newDate.getDate() + i);
      this.dates.push(newDate);
    }
  }

  fetchMovieDetails() {
    const movieId = this.route.snapshot.paramMap.get('id');
    console.log('Movie ID:', movieId); // Check if movieId is being passed
    if (movieId) {
      this.http.get<any[]>('assets/movies.json')
        .pipe(map(movies => {
          const foundMovie = movies.find(movie => movie.id === movieId);
          console.log('Found Movie:', foundMovie); // Log the found movie
          return foundMovie;
        }))
        .subscribe(movie => {
          this.movie = movie;
          console.log('Movie Title:', this.movie?.title); // Check if the title is assigned
        });
    }
  }

  selectDate(date: Date) {
    console.log('Date selected:', date);
  }

  selectShowtime(cinema: any, time: string) {
    console.log('Showtime selected:', cinema, time);
  }
}
<div class="ticket-booking">
  <h1>{{ movie.title }},</h1>
  <p>{{movie.genre}}</p>

  <!-- Date Selection -->
  <div class="date-selector">
    <button *ngFor="let date of dates" class="date-button" (click)="selectDate(date)">
      {{ date | date:'EEE, MMM d' }}
    </button>
  </div>

  <!-- Cinema and Showtimes Selection -->
  <div *ngFor="let cinema of cinemas" class="cinema">
    <div class="cinema-name">{{ cinema.name }}</div>
    <div class="showtimes">
      <button *ngFor="let showtime of cinema.showtimes" class="showtime-button" (click)="selectShowtime(showtime, cinema)">
        {{ showtime }}
      </button>
    </div>
  </div>

  <!-- Ticket Number Popup -->
  <div *ngIf="selectedShowtime">
    <p>Select number of tickets for {{ selectedDate | date:'EEE, MMM d' }} at {{ selectedShowtime }}:</p>
    <input type="number" [(ngModel)]="numberOfTickets" min="1" max="10">
    <button (click)="goToSeatSelection()">Select Seats</button>
  </div>
</div>
