// Importing necessary modules from Angular core and router
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';  // Ensure this is correctly imported

@Component({
  selector: 'app-book-tickets',  // This selector is how you'll reference this component in HTML
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any;  // This would be better typed based on your actual data structure
  dates: Date[] = [];  // Example placeholder for dates
  cinemas: any[] = [];  // Placeholder for cinemas, should also be typed
  selectedDate: Date | null = null;
  selectedShowtime: string | null = null;
  numberOfTickets: number = 1;  // Default number of tickets

  constructor(private router: Router) {
    // The router is used for navigating between components
  }

  ngOnInit() {
    // Initialization code, fetch data needed for component here
  }

  selectDate(date: Date) {
    // Method to handle date selection
    this.selectedDate = date;
    // Possibly reset other selections depending on your UX design
  }

  selectShowtime(showtime: string, cinema: any) {
    // Method to handle showtime selection
    this.selectedShowtime = showtime;
    // Update further UI components or states as necessary
  }

  goToSeatSelection() {
    // Method to navigate to the seat selection component
    this.router.navigate(['/seat-selection'], {
      state: { 
        date: this.selectedDate,
        showtime: this.selectedShowtime,
        tickets: this.numberOfTickets
      }
    });
  }
}
<div class="ticket-booking">
  <h1>{{ movie.title }},</h1>
  <p>{{movie.genre}}</p>

  <!-- Date Selection -->
  <div class="date-selector">
    <button *ngFor="let date of dates" class="date-button" (click)="selectDate(date)">
      {{ date | date:'EEE, MMM d' }}
    </button>
  </div>

  <!-- Cinema and Showtimes Selection -->
  <div *ngFor="let cinema of cinemas" class="cinema">
    <div class="cinema-name">{{ cinema.name }}</div>
    <div class="showtimes">
      <button *ngFor="let showtime of cinema.showtimes" class="showtime-button" (click)="selectShowtime(showtime, cinema)">
        {{ showtime }}
      </button>
    </div>
  </div>

  <!-- Ticket Number Popup -->
  <div *ngIf="selectedShowtime">
    <p>Select number of tickets for {{ selectedDate | date:'EEE, MMM d' }} at {{ selectedShowtime }}:</p>
    <input type="number" [(ngModel)]="numberOfTickets" min="1" max="10">
    <button (click)="goToSeatSelection()">Select Seats</button>
  </div>
</div>
