import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {

  movie: any;
  selectedShowtime: string = ''; // Initialize the showtime as empty by default
  currentDate: Date = new Date(); // Get the current date
  selectedDate: Date = this.currentDate;

  movieTheaters = [
    { 
      name: "AAA Cinemas: Ameerpet", 
      timings: ["10:00 AM", "01:00 PM", "04:00 PM", "07:00 PM"]
    },
    { 
      name: "Pratap Theatre: Langer House", 
      timings: ["09:00 AM", "12:00 PM", "03:00 PM", "06:00 PM"]
    },
    { 
      name: "AMB Cinemas: Gachibowli", 
      timings: ["11:00 AM", "02:00 PM", "05:00 PM", "08:00 PM"]
    }
  ];

  constructor(private router: Router, private route: ActivatedRoute) {}

  ngOnInit(): void {
    // Ensure that the selected movie is fetched from the navigation state
    const navigation = this.router.getCurrentNavigation();
    this.movie = navigation?.extras.state?.movie;

    // Set the default showtime if needed, otherwise can be set dynamically
    this.selectedShowtime = this.movieTheaters[0]?.timings[0];
  }

  onSelectShowtime(showtime: string): void {
    this.selectedShowtime = showtime;
  }

  // To handle further redirection if needed
  onProceedToSeats() {
    this.router.navigate(['/seat-selection'], { state: { movie: this.movie, showtime: this.selectedShowtime } });
  }
}
<!-- Book Tickets Component Template -->
<div class="movie-header">
  <h1>{{ movie?.title }} - {{ movie?.genre }}</h1>
  <div class="date-selector">
    <button *ngFor="let date of dates" (click)="selectDate(date)">{{ date | date:'EEE, dd MMM' }}</button>
  </div>
</div>

<div class="cinemas-list">
  <ng-container *ngFor="let cinema of cinemas">
    <div class="cinema">
      <h3>{{ cinema.name }}</h3>
      <div class="showtimes">
        <button *ngFor="let time of cinema.showtimes" (click)="selectShowtime(cinema, time)">
          {{ time }}
        </button>
      </div>
    </div>
  </ng-container>
</div>
