import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { MovieService } from '../services/movie.service';
import { Movie } from '../models/movie.model';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: Movie;
  theaters: any[]; // Example structure, define properly based on your data

  constructor(
    private movieService: MovieService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const id = +params.get('id'); // or another way you pass the movie ID
      this.movieService.getMovieById(id).subscribe(movie => {
        this.movie = movie;
      });
    });

    // Hardcoded or fetch from a service
    this.theaters = [
      { name: 'Theater 1', times: ['12:00', '15:00', '18:00'] },
      { name: 'Theater 2', times: ['13:00', '16:00', '19:00'] }
    ];
  }
}
<h1>{{ movie?.title }}</h1>
<div *ngFor="let theater of theaters">
  <h2>{{ theater.name }}</h2>
  <ul>
    <li *ngFor="let time of theater.times">
      {{ time }}
    </li>
  </ul>
</div>
