import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any; // You'll replace 'any' with the appropriate type or interface
  dates = this.generateWeekDates();
  cinemas: any[]; // Type accordingly or replace with real type
  selectedDate: Date = new Date();
  selectedShowtime: { time: string, type: string } | null = null;
  showSeatSelectionModal = false;
  numberOfSeats = 1;

  constructor(private route: ActivatedRoute, private router: Router) { }

  ngOnInit(): void {
    this.movie = this.route.snapshot.data['movie'];
    // Assuming cinemas are part of the movie data or fetched separately
    this.cinemas = this.movie.cinemas;
  }

  generateWeekDates() {
    const dates = [];
    const today = new Date();
    for (let i = 0; i < 7; i++) {
      const nextDate = new Date(today);
      nextDate.setDate(today.getDate() + i);
      dates.push(nextDate);
    }
    return dates;
  }

  selectShowTime(time: { time: string, type: string }) {
    this.selectedShowtime = time;
    this.showSeatSelectionModal = true;
  }

  onNumberOfSeatsChange(value: string) {
    this.numberOfSeats = parseInt(value, 10);
  }

  confirmSeatSelection() {
    this.showSeatSelectionModal = false;
    // Navigate to seat selection with the selected showtime and number of seats
    this.router.navigate(['/seat-selection'], {
      queryParams: {
        date: this.selectedDate.toISOString(),
        time: this.selectedShowtime?.time,
        seats: this.numberOfSeats
      }
    });
  }
}
<div class="ticket-booking">
  <h1>{{ movie.title }}</h1>
  <p>{{movie.genre}}</p>

  <div class="date-selector">
    <button *ngFor="let date of dates" class="date-button">
      {{ date | date:'EEE, MMM d' }}
    </button>
  </div>

  <div *ngFor="let cinema of cinemas" class="cinema">
    <div class="cinema-name">{{ cinema.name }}</div>
    <div class="showtimes">
      <button *ngFor="let showtime of cinema.showtimes" class="showtime-button">
        {{ showtime }}
      </button>
    </div>
  </div>
  <div *ngIf="movie">
    <h1>{{ movie.title }}</h1>
    <p>Select a show time:</p>
    <div *ngFor="let time of movie.showTimes">
      <button (click)="selectShowTime(time)">{{ time.time }} - {{ time.type }}</button>
    </div>

    <div *ngIf="showSeatSelectionModal" class="modal">
      <div class="modal-content">
        <span class="close" (click)="showSeatSelectionModal = false">&times;</span>
        <h2>How Many Seats?</h2>
        <input type="number" [(ngModel)]="numberOfSeats" (change)="onNumberOfSeatsChange($event.target.value)" min="1">
        <button (click)="confirmSeatSelection()">Select Seats</button>
      </div>
    </div>
  </div>

</div>
