import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'cinebook_app';
}
<nav class="navbar">
  <div class="brand">
    <!-- Wrap CineBook with a link that routes to /home -->
    <h1><a routerLink="/home" style="color: inherit; text-decoration: none;">Cinebook</a></h1>
  </div>
  <div class="nav-links">
    <a routerLink="/home">Home</a>
    <a routerLink="/login" class="nav-item">Login</a>
  </div>
</nav>

<router-outlet></router-outlet>
