import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Location } from '@angular/common';

// Assuming these types are defined globally or imported from a types file
interface Movie {
  title: string;
}

interface Cinema {
  name: string;
}

interface Seat {
  booked: boolean;
  label: string;
}

interface Showtime {
  start: Date;
}

@Component({
  selector: 'app-seat-selection',
  templateUrl: './seat-selection.component.html',
  styleUrls: ['./seat-selection.component.css']
})
export class SeatSelectionComponent implements OnInit {
  movie: Movie;
  selectedCinema: Cinema;
  selectedDate: Date = new Date();
  selectedShowtime: Showtime;
  seatMap: Seat[][];
  selectedSeats: { row: number, col: number }[] = [];

  constructor(private router: Router, private route: ActivatedRoute, ) {
    // Initialize with default values or fetch from a service or route data
    this.movie = { title: "Example Movie" };
    this.selectedCinema = { name: "Cinema Name" };
    this.selectedShowtime = { start: new Date() };
    this.seatMap = this.generateSeatMap(10, 12); // 10 rows, 12 seats per row
  }

  ngOnInit(): void {}

  generateSeatMap(rows: number, cols: number): Seat[][] {
    let map = [];
    for (let i = 0; i < rows; i++) {
      let row = [];
      for (let j = 0; j < cols; j++) {
        row.push({ booked: Math.random() < 0.2, label: String.fromCharCode(65 + i) + (j + 1) });
      }
      map.push(row);
    }
    return map;
  }

  toggleSeat(rowIndex: number, seatIndex: number): void {
    if (this.seatMap[rowIndex][seatIndex].booked) return;
    let seatFound = this.selectedSeats.find(s => s.row === rowIndex && s.col === seatIndex);
    if (seatFound) {
      this.selectedSeats = this.selectedSeats.filter(s => s !== seatFound);
    } else {
      this.selectedSeats.push({ row: rowIndex, col: seatIndex });
    }
  }

  isSelectedSeat(rowIndex: number, seatIndex: number): boolean {
    return !!this.selectedSeats.find(s => s.row === rowIndex && s.col === seatIndex);
  }

  confirmSelection(): void {
    // Process selection confirmation
    console.log('Selected seats:', this.selectedSeats);
    this.router.navigate(['/confirmation']);
  }
}
