import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any;  // This should be filled by fetching data from your JSON file
  dates: Date[] = [];
  cinemas: any[] = [
    { name: 'AAA Cinemas: Ameerpet', showtimes: ['12:00 PM', '3:55 PM', '10:30 PM'] },
    { name: 'Alankar (Pratap Theatre): Langer House', showtimes: ['2:00 PM', '9:00 PM'] },
    { name: 'AMB Cinemas: Gachibowli', showtimes: ['11:45 AM', '3:50 PM', '6:25 PM'] }
  ];

  constructor() {}

  ngOnInit(): void {
    this.loadDates();
    this.loadMovieData();
  }

  loadDates() {
    let today = new Date();
    for (let i = 0; i < 7; i++) {
      let newDate = new Date(today);
      newDate.setDate(today.getDate() + i);
      this.dates.push(newDate);
    }
  }

  loadMovieData() {
    // Load movie data here, assuming you have access to a service that fetches data
    this.movie = {
      title: 'Devara - Part 1',
      genre: 'Action, Drama, Thriller'
    };
  }

  selectDate(date: Date) {
    console.log('Date selected:', date);
  }

  selectShowtime(cinema: any, time: string) {
    console.log('Showtime selected:', cinema, time);
  }
}
<h1>{{ movie?.title }}</h1>
<div *ngFor="let theater of theaters">
  <h2>{{ theater.name }}</h2>
  <ul>
    <li *ngFor="let time of theater.times">
      {{ time }}
    </li>
  </ul>
</div>
