import { Component, OnInit } from '@angular/core';
import { MovieService } from '../services/movie.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  movies: any[] = []; // Will hold the movie data

  constructor(private movieService: MovieService) {}

  ngOnInit(): void {
    // Fetch movie data from the JSON file using the MovieService
    this.movieService.getMovies().subscribe((data: any[]) => {
      this.movies = data;
    });
  }

  // Function to slide the movie list
  slideMovies(direction: string): void {
    const slider = document.getElementById('movieSlider');
    const scrollAmount = 300; // Amount to scroll (adjust as per your design)
    if (direction === 'right' && slider) {
      slider.scrollBy({ left: scrollAmount, behavior: 'smooth' });
    } else if (direction === 'left' && slider) {
      slider.scrollBy({ left: -scrollAmount, behavior: 'smooth' });
    }
  }
}
<div class="home-container">
  <h1>Welcome to Movie Ticket Booking</h1>

  <div class="banner">
    <h2>Now Showing</h2>
  </div>

  <div class="movies-section">
    <h2>Movies</h2>
    <div class="movie-slider-wrapper">
      <button class="slider-btn left-btn" (click)="slideMovies('left')">&#10094;</button>

      <!-- Movie Slider with 4 visible movies at a time -->
      <div class="movie-slider" id="movieSlider">
        <div class="movie-card" *ngFor="let movie of movies">
  <a [routerLink]="['/book-tickets', movie.id]">
    <img [src]="movie.image" alt="{{ movie.title }}">
    <h5>{{ movie.title }}</h5>
  </a>
</div>
      </div>

      <button class="slider-btn right-btn" (click)="slideMovies('right')">&#10095;</button>
    </div>
  </div>
</div>
