import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router, NavigationExtras } from '@angular/router';
import { Location } from '@angular/common';
import { MovieService } from '../services/movie.service';
import { CinemaService } from '../services/cinema.service';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any;
  cinemas: any[] = [];
  selectedDate: Date = new Date();
  selectedShowtime: string = '';
  dates: Date[] = [];

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private location: Location,
    private movieService: MovieService,
    private cinemaService: CinemaService
  ) {}

  ngOnInit(): void {
    const movieId = this.route.snapshot.paramMap.get('id');

    // Ensure the movieId is not null or undefined before using it
    if (movieId !== null) {
      const movieIndex = +movieId; // Convert movieId to number (array index)

      // Fetch movie details based on the movie ID from the JSON file
      this.movieService.getMovies().subscribe((movies: any[]) => {
        if (movies && movieIndex >= 0 && movieIndex < movies.length) {
          this.movie = movies[movieIndex]; // Fetch the movie using the index
        }
      });
    }
    this.generateWeekDates();
    this.loadCinemas();
    this.loadMovies();
  }

  loadMovies() {
    this.movieService.getMovies().subscribe(movies => {
      this.movie = movies[0]; // Load the first movie or based on some condition
    });
  }

  generateWeekDates() {
    for (let i = 0; i < 7; i++) {
      const date = new Date();
      date.setDate(this.selectedDate.getDate() + i);
      this.dates.push(date);
    }
  }

  loadCinemas() {
    this.cinemaService.getCinemas().subscribe(data => {
      this.cinemas = data;
    }, error => {
      console.error('Error fetching cinemas:', error);
    });
  }

  selectDate(date: Date) {
    this.selectedDate = date;
  }

  selectShowtime(cinema: any, showtime: string) {
    const navigationExtras: NavigationExtras = {
      state: {
        movie: this.movie,
        cinema: cinema,
        date: this.selectedDate,
        showtime: showtime
      }
    };
    this.router.navigate(['/seat-selection'], navigationExtras);
  }

  goBack() {
    this.location.back();
  }
}
<!-- Back Button -->
<button class="back-button" (click)="goBack()">Back</button>

<div *ngIf="movie" class="movie-details">
  <div class="movie-details-container">
    <!-- Movie Poster -->
    <div class="movie-image">
      <img [src]="movie.image" alt="{{ movie.title }}">
    </div>

    <!-- Movie Title and Details -->
    <div class="movie-info">
      <h1>{{ movie.title }}</h1>
      <p><strong>Genre:</strong> {{ movie.genre }}</p>
    </div>
  </div>
</div>
<!-- Movie Information -->
<div class="ticket-booking">

  <!-- Date Selector -->
  <div class="date-selector">
    <button *ngFor="let date of dates" (click)="selectDate(date)" 
            [ngClass]="{'selected': date === selectedDate}" class="date-button">
      {{ date | date:'EEE, MMM d' }}
    </button>
  </div>

  <!-- Cinema and Showtimes Selector -->
  <div *ngFor="let cinema of cinemas" class="cinema">
    <div class="cinema-name">{{ cinema.name }}</div>
    <div class="showtimes">
      <button *ngFor="let showtime of cinema.showtimes" (click)="selectShowtime(cinema, showtime)" 
              class="showtime-button">
        {{ showtime }}
      </button>
    </div>
  </div>
</div>
