import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router, NavigationExtras } from '@angular/router';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any;  // Consider using a specific type or interface if possible
  cinemas = [
    { name: 'Cinema One', showtimes: ['9:00 AM', '12:00 PM', '3:00 PM', '6:00 PM'] },
    { name: 'Cinema Two', showtimes: ['10:00 AM', '1:00 PM', '4:00 PM', '7:00 PM'] }
  ];
  selectedDate: Date = new Date();
  dates: Date[] = [];

  constructor(private router: Router, private route: ActivatedRoute) {
    const navigation = this.router.getCurrentNavigation();
    this.movie = navigation?.extras.state ? navigation.extras.state['movie'] : null;
  }

  ngOnInit(): void {
    this.generateWeekDates();
  }

  generateWeekDates() {
    for (let i = 0; i < 7; i++) {
      const date = new Date();
      date.setDate(this.selectedDate.getDate() + i);
      this.dates.push(date);
    }
  }
}
