import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-seat-selection',
  templateUrl: './seat-selection.component.html',
  styleUrls: ['./seat-selection.component.css']
})
export class SeatSelectionComponent implements OnInit {
  movie: any;
  cinema: any;
  date: any;
  showtime: any;

  constructor(private route: ActivatedRoute, private router: Router) {}

  ngOnInit(): void {
    // Using navigation extras to get the data passed from the previous page
    this.route.queryParams.subscribe(params => {
      if (this.router.getCurrentNavigation().extras.state) {
        const state = this.router.getCurrentNavigation().extras.state;
        this.movie = state.movie;
        this.cinema = state.cinema;
        this.date = state.date;
        this.showtime = state.showtime;
      }
    });
  }

  isSeatTaken(row: string, seat: number): boolean {
    // Simulate seat availability, adapt as necessary
    return (seat % 2 === 0); // Example: Even seats are taken
  }
}
