import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { MovieService } from './services/movie.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  searchText: string = '';
  filteredMovies: any[] = [];

  constructor(private movieService: MovieService, private router: Router) {}

  ngOnInit(): void {}

  onSearchChange(): void {
    if (this.searchText) {
      this.movieService.getMovies().subscribe((data: any[]) => {
        this.filteredMovies = data.filter(movie =>
          movie.title.toLowerCase().includes(this.searchText.toLowerCase()));
      });
    } else {
      this.filteredMovies = [];
    }
  }

  navigateToMovieDetails(movieId: string): void {
    this.router.navigate(['/movie', movieId]);
    this.searchText = ''; // Clear the search input after navigation
    this.filteredMovies = [];
  }
}
<nav class="navbar">
  <div class="brand">
    <h1><a routerLink="/home" style="color: inherit; text-decoration: none;">Cinebook</a></h1>
  </div>
  <div class="nav-links">
    <input type="text" [(ngModel)]="searchText" (input)="onSearchChange()" placeholder="Search movies...">
    <div class="dropdown-content" *ngIf="searchText">
      <a *ngFor="let movie of filteredMovies" (click)="navigateToMovieDetails(movie.id)">{{ movie.title }}</a>
    </div>
    <a routerLink="/home">Home</a>
    <a routerLink="/login" class="nav-item">Login</a>
  </div>
</nav>
<router-outlet></router-outlet>
