import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router, NavigationExtras } from '@angular/router';
import { Location } from '@angular/common';
import { MovieService } from '../services/movie.service';
import { CinemaService } from '../services/cinema.service';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any;
  cinemas: any[] = [];
  selectedDate: Date = new Date();
  selectedShowtime: string = '';
  dates: Date[] = [];

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private location: Location,
    private movieService: MovieService,
    private cinemaService: CinemaService
  ) {
    const navigation = this.router.getCurrentNavigation();
    this.movie = navigation?.extras.state ? navigation.extras.state['movie'] : null;
  }

  ngOnInit(): void {
    this.generateWeekDates();
    this.loadCinemas();
  }


  generateWeekDates() {
    let today = new Date();
    today = new Date(today.getFullYear(), today.getMonth(), today.getDate()); // Normalize today to have no time component
    this.selectedDate = new Date(today); // Set selectedDate to today
    for (let i = 0; i < 7; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      this.dates.push(date);
    }
  }

  loadCinemas() {
    this.cinemaService.getCinemas().subscribe(data => {
      this.cinemas = data;
    }, error => {
      console.error('Error fetching cinemas:', error);
    });
  }

  selectDate(date: Date) {
    this.selectedDate = new Date(date.getFullYear(), date.getMonth(), date.getDate()); // Normalize selected date
  }
  isSelected(date: Date): boolean {
    return this.selectedDate.getDate() === date.getDate() &&
           this.selectedDate.getMonth() === date.getMonth() &&
           this.selectedDate.getFullYear() === date.getFullYear();
  }

  selectShowtime(cinema: any, showtime: string) {
    const navigationExtras: NavigationExtras = {
      state: {
        movie: this.movie,
        cinema: cinema,
        date: this.selectedDate,
        showtime: showtime
      }
    };
    this.router.navigate(['/seat-selection'], navigationExtras);
  }

  goBack() {
    this.location.back();
  }
}
<!-- Back Button -->
<button class="back-button" (click)="goBack()">Back</button>

<!-- book-tickets.component.html -->
<div class="content-box">
  <div class="ticket-booking">
      <div class="movie-image">
      <img [src]="movie.image" alt="{{ movie.title }}">
      </div>
      <div class="movie-details">
          <h1>{{ movie.title }}</h1>
          <p>{{ movie.genre }}</p>
      </div>

      <!-- Date Selector Merged with Cinema Listings -->
      <div class="cinema-date-list">
          <div class="date-selector">
              <button *ngFor="let date of dates" (click)="selectDate(date)"
                      [ngClass]="{'selected': isSelected(date)}" class="date-button">
                  {{ date | date:'EEE, MMM d' }}
              </button>
          </div>

          <!-- Cinema Listings and Showtimes -->
          <div *ngFor="let cinema of cinemas; let i = index" class="cinema">
              <div class="cinema-name">{{ cinema.name }}</div>
              <div class="showtimes">
                  <button *ngFor="let showtime of cinema.showtimes" (click)="selectShowtime(cinema, showtime)"
                          class="showtime-button">
                      {{ showtime }}
                  </button>
              </div>
              <hr *ngIf="i < cinemas.length - 1"> <!-- Line separator except for the last item -->
          </div>
      </div>
  </div>
</div>
