import { Component, Input } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';


@Component({
  selector: 'app-book',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})


export class BookComponent {

  movieSelected: any;
  id: any;
  seatConfig: any = null;
  seatmap = [];
  seatChartConfig = {
    showRowsLabel: true,
    showRowWisePricing: true,
    newSeatNoForRow: true
  }
  cart = {
    selectedSeats: [],
    seatstoStore: [],
    totalamount: 0,
    cartId: "",
    eventId: 0
  };


  title = 'seat-chart-generator';
  bookForm: any;

  constructor(private http: HttpClient,private router:Router) { }


  ngOnInit(): void {
// theatre layout
    this.seatConfig = [
      {
        "seat_price": 120,
        "seat_map": [
          {
            "seat_label": "N",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "M",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "L",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "K",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "J",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "I",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "H",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": " ",
            "layout": "__________________________________"
          }
        ]
      },
      {
        "seat_price": 200,
        "seat_map": [
          {
            "seat_label": "G",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "F",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "E",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "D",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "C",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "B",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": "A",
            "layout": "_ccccccccccccccc_cccccccccccccccc_"
          },
          {
            "seat_label": " ",
            "layout": "__________________________________"
          }
        ]
      }
    ];

    this.processSeatChart(this.seatConfig);
  }

  public processSeatChart(map_data: any[]) {

    if (map_data.length > 0) {
      var seatNoCounter = 1;
      for (let __counter = 0; __counter < map_data.length; __counter++) {
        var row_label = "";
        var item_map = map_data[__counter].seat_map;

        //Get the label name and price
        row_label = "Row " + item_map[0].seat_label + " - ";
        if (item_map[item_map.length - 1].seat_label != " ") {
          row_label += item_map[item_map.length - 1].seat_label;
        }
        else {
          row_label += item_map[item_map.length - 2].seat_label;
        }
        row_label += " : Rs. " + map_data[__counter].seat_price;

        item_map.forEach(map_element => {
          var mapObj = {
            "seatRowLabel": map_element.seat_label,
            "seats": [],
            "seatPricingInformation": row_label
          };
          row_label = "";
          var seatValArr = map_element.layout.split('');
          if (this.seatChartConfig.newSeatNoForRow) {
            seatNoCounter = 1; //Reset the seat label counter for new row
          }
          var totalItemCounter = 1;
          seatValArr.forEach(item => {
            var seatObj = {
              "key": map_element.seat_label + "_" + totalItemCounter,
              "price": map_data[__counter]["seat_price"],
              "status": "available"
            };

            if (item != '_') {
              seatObj["seatLabel"] = map_element.seat_label + " " + seatNoCounter;
              if (seatNoCounter < 10) { seatObj["seatNo"] = "0" + seatNoCounter; }
              else { seatObj["seatNo"] = "" + seatNoCounter; }

              seatNoCounter++;
            }
            else {
              seatObj["seatLabel"] = "";
            }
            totalItemCounter++;
            mapObj["seats"].push(seatObj);
          });
          console.log(" \n\n\n Seat Objects ", mapObj);
          this.seatmap.push(mapObj);

        });
      }
    }
  }

  public selectSeat(seatObject: any) {
    console.log("Seat to block: ", seatObject);
    if (seatObject.status == "available") {
      seatObject.status = "booked";
      this.cart.selectedSeats.push(seatObject.seatLabel);
      this.cart.totalamount += seatObject.price;

    }
    else if (seatObject.status = "booked") {
      seatObject.status = "available";
      var seatIndex = this.cart.selectedSeats.indexOf(seatObject.seatLabel);
      if (seatIndex > -1) {
        this.cart.selectedSeats.splice(seatIndex, 1);
        this.cart.totalamount -= seatObject.price;

      }

    }
  }

  public blockSeats(seatsToBlock: string) {
    if (seatsToBlock != "") {
      var seatsToBlockArr = seatsToBlock.split(',');
      for (let index = 0; index < seatsToBlockArr.length; index++) {
        var seat = seatsToBlockArr[index] + "";
        var seatSplitArr = seat.split("_");
        console.log("Split seat: ", seatSplitArr);
        for (let index2 = 0; index2 < this.seatmap.length; index2++) {
          const element = this.seatmap[index2];
          if (element.seatRowLabel == seatSplitArr[0]) {
            var seatObj = element.seats[parseInt(seatSplitArr[1]) - 1];
            if (seatObj) {
              console.log("\n\n\nFount Seat to block: ", seatObj);
              seatObj["status"] = "unavailable";
              this.seatmap[index2]["seats"][parseInt(seatSplitArr[1]) - 1] = seatObj;
              console.log("\n\n\nSeat Obj", seatObj);
              console.log(this.seatmap[index2]["seats"][parseInt(seatSplitArr[1]) - 1]);
              break;
            }

          }
        }

      }
    }

  }


}






<div style="text-align:center">
</div>
<div class="col-lg-12 seat-container">
    <h3>
        Seat Chart Layout
    </h3>
    <h3>
        Seat Chart Layout
    </h3>


    <div *ngFor="let seatrow of seatmap ">
        <div class="col-12 pricing-label"
            *ngIf="seatChartConfig?.showRowWisePricing && seatrow.seatPricingInformation != ''">
            {{seatrow.seatPricingInformation}}</div>
        <div class="seatrow container">
            <div *ngIf="seatChartConfig?.showRowsLabel">
                <div class="row-label"> {{seatrow.seatRowLabel}} </div>
            </div>
            <div *ngFor="let seatobj of seatrow.seats">
                <div class="seat-item seat-space" style="color:white ; background-color:white"
                    *ngIf="seatobj.seatLabel == ''"> &nbsp; </div>
                <div tooltip="Seat : {{seatobj.seatLabel}} | Price : {{seatobj.price}}Rs" placement="top"
                    [ngClass]="{ 'seat-item' : true , 'seat-available' : seatobj.status == 'available' ,'seat-booked' : seatobj.status == 'booked' ,'seat-unavailable' : seatobj.status == 'unavailable' }"
                    (click)="selectSeat(seatobj)"
                    *ngIf="(seatobj.status == 'available' || seatobj.status == 'booked' )&& seatobj.seatLabel != ''">
                    {{seatobj.seatNo}}</div>

                <div tooltip="Seat not available" placement="top"
                    [ngClass]="{ 'seat-item' : true , 'seat-unavailable' : true }"
                    *ngIf="seatobj.status == 'unavailable' && seatobj.seatLabel != ''">{{seatobj.seatNo}}</div>

