import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router'; // Import Router for navigation
import { MovieService } from '../services/movie.service'; // Import MovieService for fetching movie details

@Component({
  selector: 'app-movie-details',
  templateUrl: './movie-details.component.html',
  styleUrls: ['./movie-details.component.css']
})
export class MovieDetailsComponent implements OnInit {

  movie: any; // Holds the movie details

  constructor(
    private route: ActivatedRoute, // For handling route params if needed
    private router: Router,        // For navigating between pages
    private movieService: MovieService // Movie service for fetching movie details
  ) {}

  ngOnInit(): void {
    // Fetch the movie details - this can be dynamic or static data
    // You might want to fetch this based on an ID in the route params
    // Here it's hardcoded for simplicity
    this.movie = {
      title: "Sample Movie",
      description: "A thrilling movie about something exciting!",
      releaseDate: "2024-09-17",
      genre: "Action/Adventure",
      image: "https://via.placeholder.com/300"
    };
  }

  onBookTickets() {
    // Navigate to the booking page (showtime and seat selection)
    // Passing the movie object to the next route using state
    this.router.navigate(['/book-tickets'], { state: { movie: this.movie } });
  }
}
