import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { MovieService } from '../services/movie.service';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any;
  dates: Date[] = [];
  cinemas: any[] = [];
  selectedDate: Date | null = null;
  selectedCinema: any = null;
  selectedShowtime: string | null = null;

  constructor(private route: ActivatedRoute, private movieService: MovieService, private router: Router) {}

  ngOnInit(): void {
    // Extract movie data from navigation state
    const navigation = this.router.getCurrentNavigation();
    this.movie = navigation?.extras.state?.['movie'];

    // Set the available dates for one week from today
    const today = new Date();
    for (let i = 0; i < 7; i++) {
      const nextDate = new Date(today);
      nextDate.setDate(today.getDate() + i);
      this.dates.push(nextDate);
    }

    // Example cinemas data
    this.cinemas = [
      {
        name: 'AAA Cinemas',
        showtimes: ['10:00 AM', '1:00 PM', '4:00 PM', '7:00 PM', '10:00 PM']
      },
      {
        name: 'Alankar Theatre',
        showtimes: ['11:00 AM', '2:00 PM', '5:00 PM', '8:00 PM']
      },
      {
        name: 'AMB Cinemas',
        showtimes: ['12:00 PM', '3:00 PM', '6:00 PM', '9:00 PM']
      }
    ];
  }

  selectDate(date: Date): void {
    this.selectedDate = date;
  }

  selectShowtime(cinema: any, showtime: string): void {
    this.selectedCinema = cinema;
    this.selectedShowtime = showtime;

    // Proceed to show the seat selection pop-up
    if (this.selectedDate && this.selectedShowtime) {
      // Navigate to seat selection page with the chosen movie, cinema, date, and showtime
      this.router.navigate(['/seat-selection'], {
        state: { 
          movie: this.movie, 
          selectedDate: this.selectedDate,
          selectedCinema: this.selectedCinema,
          selectedShowtime: this.selectedShowtime
        }
      });
    }
  }
}
<div class="ticket-booking">
  <h1>{{ movie.title }},</h1>
  <p>{{movie.genre}}</p>

  <div class="date-selector">
    <button *ngFor="let date of dates" class="date-button">
      {{ date | date:'EEE, MMM d' }}
    </button>
  </div>

  <div *ngFor="let cinema of cinemas" class="cinema">
    <div class="cinema-name">{{ cinema.name }}</div>
    <div class="showtimes">
      <button *ngFor="let showtime of cinema.showtimes" class="showtime-button">
        {{ showtime }}
      </button>
    </div>
  </div>
</div>
