import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  movies = [
    { title: 'Movie 1', image: 'https://via.placeholder.com/150' },
    { title: 'Movie 2', image: 'https://via.placeholder.com/150' },
    { title: 'Movie 3', image: 'https://via.placeholder.com/150' },
    { title: 'Movie 4', image: 'https://via.placeholder.com/150' },
    { title: 'Movie 5', image: 'https://via.placeholder.com/150' },
    { title: 'Movie 6', image: 'https://via.placeholder.com/150' },
    { title: 'Movie 7', image: 'https://via.placeholder.com/150' },
    { title: 'Movie 8', image: 'https://via.placeholder.com/150' },
    { title: 'Movie 9', image: 'https://via.placeholder.com/150' },
    { title: 'Movie 10', image: 'https://via.placeholder.com/150' },
  ];

  constructor() { }

  ngOnInit(): void {
  }

  slideMovies(direction: string): void {
    const slider = document.getElementById('movieSlider');
    const scrollAmount = direction === 'right' ? 300 : -300;
    if (slider) {
      slider.scrollBy({ left: scrollAmount, behavior: 'smooth' });
    }
  }
}
