// seat-selection.component.ts
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-seat-selection',
  templateUrl: './seat-selection.component.html',
  styleUrls: ['./seat-selection.component.css']
})
export class SeatSelectionComponent implements OnInit {
  selectedCinema = { name: 'AAA Cinemas: Ameerpet' };
  selectedDate = new Date();
  selectedShowtime = '09:00 PM';
  movie = { title: 'Devara - Part 1' };

  seatMap = []; // Assume this is fetched or constructed elsewhere
  selectedSeats = [];

  constructor() {}

  ngOnInit() {
    // Initialize your seat map here
    this.initializeSeatMap();
  }

  initializeSeatMap() {
    // Example initialization, adapt based on actual data structure
    this.seatMap = new Array(10).fill(new Array(12).fill({ booked: false, label: '' })).map((row, rowIndex) =>
      row.map((seat, seatIndex) => ({
        ...seat,
        label: String.fromCharCode(65 + rowIndex) + (seatIndex + 1),
        booked: Math.random() < 0.2  // Randomly mark some seats as booked
      }))
    );
  }

  toggleSeat(rowIndex: number, seatIndex: number) {
    const seat = this.seatMap[rowIndex][seatIndex];
    if (seat.booked) return;

    const isSelected = this.isSelectedSeat(rowIndex, seatIndex);
    if (isSelected) {
      this.selectedSeats = this.selectedSeats.filter(s => !(s.row === rowIndex && s.col === seatIndex));
    } else {
      this.selectedSeats.push({ row: rowIndex, col: seatIndex });
    }
  }

  isSelectedSeat(rowIndex: number, seatIndex: number): boolean {
    return this.selectedSeats.some(seat => seat.row === rowIndex && seat.col === seatIndex);
  }

  confirmSelection() {
    console.log('Selected seats:', this.selectedSeats);
    // Proceed with the booking or further confirmation steps
  }
}
