import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

interface Seat {
  booked: boolean;
  label: string;
}

interface SelectedSeat {
  row: number;
  col: number;
}

interface Cinema {
  name: string;
}

interface Movie {
  title: string;
  language: string;
}

@Component({
  selector: 'app-seat-selection',
  templateUrl: './seat-selection.component.html',
  styleUrls: ['./seat-selection.component.css']
})
export class SeatSelectionComponent implements OnInit {
  selectedCinema?: Cinema;
  selectedDate?: Date;
  selectedShowtime?: any; // Define a proper type if you have one
  movie?: Movie;
  seatMap: Seat[][] = [];
  selectedSeats: SelectedSeat[] = [];

  constructor(private route: ActivatedRoute, private router: Router) {}

  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      const routerState = this.router.getCurrentNavigation()?.extras.state as
        { selectedCinema?: Cinema, selectedDate?: Date, showtime?: any, movie?: Movie };
      if (routerState) {
        this.selectedCinema = routerState['selectedCinema'];
        this.selectedDate = routerState['selectedDate'];
        this.selectedShowtime = routerState['showtime'];
        this.movie = routerState['movie'];
      }
      this.initializeSeatMap();
    });
  }

  initializeSeatMap(): void {
    this.seatMap = new Array(10).fill(null).map((_, rowIndex) => 
      new Array(12).fill(null).map((_, seatIndex) => ({
        booked: Math.random() < 0.2,
        label: String.fromCharCode(65 + rowIndex) + (seatIndex + 1)
      }))
    );
  }

  toggleSeat(rowIndex: number, seatIndex: number): void {
    const seat = this.seatMap[rowIndex][seatIndex];
    if (seat.booked) return;
    const index = this.selectedSeats.findIndex(s => s.row === rowIndex && s.col === seatIndex);
    if (index >= 0) this.selectedSeats.splice(index, 1);
    else this.selectedSeats.push({ row: rowIndex, col: seatIndex });
  }

  isSelectedSeat(rowIndex: number, seatIndex: number): boolean {
    return this.selectedSeats.some(seat => seat.row === rowIndex && seat.col === seatIndex);
  }

  confirmSelection(): void {
    console.log('Selected seats:', this.selectedSeats.map(s => `Row ${s.row + 1} Seat ${s.col + 1}`).join(', '));
  }
}
