import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-seat-selection',
  templateUrl: './seat-selection.component.html',
  styleUrls: ['./seat-selection.component.css']
})
export class SeatSelectionComponent implements OnInit {
  movie: any;
  cinema: any;
  date: any;
  showtime: any;
  takenSeats = new Set<string>(); // Tracks taken seats

  constructor(private route: ActivatedRoute, private router: Router) {}

  ngOnInit(): void {
    this.initData();
    // Simulate some seats being taken for demonstration
    this.takenSeats.add('A5');
    this.takenSeats.add('B3');
    this.takenSeats.add('C6');
  }

  initData() {
    const navigation = this.router.getCurrentNavigation();
    if (navigation && navigation.extras.state) {
      const state = navigation.extras.state as { [key: string]: any };
      this.movie = state.movie;
      this.cinema = state.cinema;
      this.date = state.date;
      this.showtime = state.showtime;
    }
  }

  isSeatTaken(row: string, seat: number): boolean {
    return this.takenSeats.has(`${row}${seat}`);
  }

  selectSeat(row: string, seat: number): void {
    if (!this.isSeatTaken(row, seat)) {
      alert(`Selected seat ${row}${seat}`);
      // Here you could toggle selection or handle the booking
    }
  }
}
<div class="movie-info">
  <h1>{{ movie.title }} ({{ showtime }})</h1>
  <h2>{{ cinema.name }} - Auditorium 10</h2>
  <p>Date: {{ date | date:'fullDate' }}</p>
  <p>Showtime: {{ showtime }}</p>
</div>

<div class="screen">Screen</div>
<div class="auditorium">
  <div class="row" *ngFor="let row of ['A', 'B', 'C', 'D', 'E', 'F']; let i = index">
    <div class="seat" *ngFor="let seat of [].constructor(10); let j = index" 
         [class.taken]="isSeatTaken(row, j+1)"
         (click)="selectSeat(row, j+1)">
      {{ row }}{{ j+1 }}
    </div>
  </div>
</div>
<button class="btn-select">Select Seats</button>
