import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Location } from '@angular/common';

interface Movie {
  title: string;
}

interface Cinema {
  name: string;
  categories: Category[];  // Adding categories here
}

interface Category {
  name: string;
  rows: Row[];
}

interface Row {
  seats: Seat[];
}

interface Seat {
  booked: boolean;
  label: string;
}

@Component({
  selector: 'app-seat-selection',
  templateUrl: './seat-selection.component.html',
  styleUrls: ['./seat-selection.component.css']
})
export class SeatSelectionComponent implements OnInit {
  movie!: Movie;
  cinema!: Cinema;
  date!: Date;
  showtime!: string;
  seatMap: Seat[][] = [];
  selectedSeats: { categoryIndex: number, rowIndex: number, colIndex: number }[] = [];

  constructor(private router: Router, private route: ActivatedRoute, private location: Location) {
    this.initializeNavigationState();
    // Assuming this needs adjustment based on new data structure
  }

  ngOnInit(): void {
    this.cinema = {
      name: "Sample Cinema",
      categories: [
        {
          name: "Rs. 295 GOLD",
          rows: Array.from({ length: 8 }, (_, rowIndex) => ({
            seats: Array.from({ length: 20 }, (_, seatIndex) => ({
              booked: Math.random() < 0.2,  // 20% seats are booked randomly
              label: `${String.fromCharCode(65 + rowIndex)}${seatIndex + 1}`
            }))
          }))
        },
        {
          name: "Rs. 295 LOUNGERS",
          rows: Array.from({ length: 2 }, (_, rowIndex) => ({
            seats: Array.from({ length: 14 }, (_, seatIndex) => ({
              booked: Math.random() < 0.2,
              label: `I${seatIndex + 1}`
            }))
          }))
        }
      ]
    };

    this.initializeNavigationState(); // Assuming this method sets other properties

  }

  initializeNavigationState() {
    const navigation = this.router.getCurrentNavigation();
    if (navigation && navigation.extras && navigation.extras.state) {
      const state = navigation.extras.state as any;
      this.movie = state.movie;
      this.cinema = state.cinema;
      this.date = state.date;
      this.showtime = state.showtime;
    }
  }

  isSelectedSeat(categoryIndex: number, rowIndex: number, seatIndex: number): boolean {
    return !!this.selectedSeats.find(s => s.categoryIndex === categoryIndex && s.rowIndex === rowIndex && s.colIndex === seatIndex);
  }

  toggleSeat(categoryIndex: number, rowIndex: number, seatIndex: number): void {
    let seat = this.cinema.categories[categoryIndex].rows[rowIndex].seats[seatIndex];
    if (seat.booked) return;

    let seatFound = this.selectedSeats.find(s => s.categoryIndex === categoryIndex && s.rowIndex === rowIndex && s.colIndex === seatIndex);
    if (seatFound) {
      this.selectedSeats = this.selectedSeats.filter(s => s !== seatFound);
    } else {
      this.selectedSeats.push({ categoryIndex, rowIndex, colIndex: seatIndex });
    }
  }

  confirmSelection(): void {
    // Process selection confirmation
    console.log('Selected seats:', this.selectedSeats);
    this.router.navigate(['/confirmation']);
  }

  goBack() {
    this.location.back();
  }
}
<button class="back-button" (click)="goBack()">Back</button>

<div class="seat-selection-container">
  <h1>{{ movie.title }}</h1>
  <div class="theater-info">
    <span>{{ cinema.name }} | {{ date | date:'fullDate' }} | {{ showtime }}</span>
  </div>
  <ng-container *ngFor="let category of cinema.categories; let catIndex = index">
    <h2>{{ category.name }}</h2>
    <div class="seat-map-container">
      <div *ngFor="let row of category.rows; let rowIndex = index" class="seat-row">
        <div *ngFor="let seat of row.seats; let seatIndex = index"
             class="seat" [ngClass]="{'available': !seat.booked, 'selected': isSelectedSeat(catIndex, rowIndex, seatIndex), 'sold': seat.booked}"
             (click)="toggleSeat(catIndex, rowIndex, seatIndex)">
          {{ seat.label }}
        </div>
      </div>
    </div>
  </ng-container>
    <div class="color-indication-bar">
      <div class="color-box available">Available</div>
      <div class="color-box selected">Selected</div>
      <div class="color-box sold">Sold</div>
    </div>
    <div class="controls">
      <button (click)="confirmSelection()" [disabled]="selectedSeats.length === 0">Confirm Selection</button>
    </div>
  </div>
