import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { map } from 'rxjs/operators';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any;
  dates: Date[] = [];
  cinemas = [
    { name: 'Cinema One', showtimes: ['9:00 AM', '12:00 PM', '3:00 PM', '6:00 PM'] },
    { name: 'Cinema Two', showtimes: ['10:00 AM', '1:00 PM', '4:00 PM', '7:00 PM'] }
  ];

  constructor(private route: ActivatedRoute, private router: Router, private http: HttpClient) {}

  ngOnInit(): void {
    this.initDates();
    this.fetchMovieDetails();
  }

  initDates() {
    for (let i = 0; i < 7; i++) {
      let newDate = new Date();
      newDate.setDate(newDate.getDate() + i);
      this.dates.push(newDate);
    }
  }

  fetchMovieDetails() {
    const movieId = this.route.snapshot.paramMap.get('id');
    if (movieId) {
      this.http.get<any[]>('assets/movies.json')
        .pipe(map(movies => movies.find(movie => movie.id === movieId)))
        .subscribe(movie => this.movie = movie);
    }
  }

  selectDate(date: Date) {
    console.log('Date selected:', date);
  }

  selectShowtime(cinema: any, time: string) {
    console.log('Showtime selected:', cinema, time);
  }
}
<!-- Book Tickets Component Template -->
<div class="movie-header">
  <h1>{{ movie?.title }} - {{ movie?.genre }}</h1>
  <div class="date-selector">
    <button *ngFor="let date of dates" (click)="selectDate(date)">{{ date | date:'EEE, dd MMM' }}</button>
  </div>
</div>

<div class="cinemas-list">
  <ng-container *ngFor="let cinema of cinemas">
    <div class="cinema">
      <h3>{{ cinema.name }}</h3>
      <div class="showtimes">
        <button *ngFor="let time of cinema.showtimes" (click)="selectShowtime(cinema, time)">
          {{ time }}
        </button>
      </div>
    </div>
  </ng-container>
</div>
