import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.css']
})
export class BookTicketsComponent implements OnInit {
  movie: any;
  dates: Date[] = [];
  cinemas: any[] = [];  // Assume this is filled as shown in previous messages

  constructor(private router: Router, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.initDates();
    this.retrieveMovieData();
  }

  initDates() {
    let today = new Date();
    for (let i = 0; i < 7; i++) {
      let newDate = new Date(today);
      newDate.setDate(today.getDate() + i);
      this.dates.push(newDate);
    }
  }

  retrieveMovieData() {
    const navigation = this.router.getCurrentNavigation();
    this.movie = navigation?.extras.state?.movie;
    if (!this.movie) {
      // Handle no movie passed error or redirect
      console.error('No movie data provided!');
    }
  }
}
<div class="movie-header">
  <h2>{{ movie?.title }} - {{ movie?.genre }}</h2>
  <div class="date-selector">
    <button *ngFor="let date of dates" (click)="selectDate(date)">
      {{ date | date:'EEE, dd MMM' }}
    </button>
  </div>
</div>
