import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { MovieService } from '../services/movie.service';
import { Movie } from '../models/movie.model';  // Ensure the Movie model is defined properly

@Component({
  selector: 'app-movie-details',
  templateUrl: './movie-details.component.html',
  styleUrls: ['./movie-details.component.css']
})
export class MovieDetailsComponent implements OnInit {
  movie: Movie | undefined;
  cinemas: any[] = [
    { name: 'Cinema City Center', showtimes: ['10:00 AM', '1:00 PM', '4:00 PM', '7:00 PM'] },
    { name: 'Downtown Cinemas', showtimes: ['11:00 AM', '2:00 PM', '5:00 PM', '8:00 PM'] }
  ];

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private movieService: MovieService
  ) {}

  ngOnInit(): void {
    this.loadMovieDetails();
  }

  loadMovieDetails(): void {
    const title = this.route.snapshot.paramMap.get('title');
    if (title) {
      this.movieService.getMovieByTitle(title).subscribe(movie => {
        this.movie = movie;
      });
    }
  }

  onBookTickets(cinema: any, showtime: string): void {
    this.router.navigate(['/seat-selection'], { state: { movie: this.movie, cinema: cinema.name, showtime } });
  }
}
